import React, { useEffect, useState } from 'react'
import './Nav.css'

function Nav() {
    const [show,setShow] = useState(false)
    useEffect(()=>{
        window.addEventListener("scr")
        if (window.scrollY>600) {
            setShow(true)
            
        }else{
            setShow(false)
        }
    })
  return (
    <div className='nav'>
        <img  width={'150px'} src="https://logodownload.org/wp-content/uploads/2014/10/netflix-logo-1-1.png" alt="netflix logo" />


    </div>
  )
}

export default Nav
